var Stylus=function(element){this.name=element
this.element=$(element)}
Stylus.prototype.addStyle=function(config){for(var i=0;i<Object.keys(config).length;i++){this.element.css(Object.keys(config)[i],Object.values(config)[i])}}
Stylus.prototype.style=function(value){return this.element.css(value)}
Stylus.prototype.inherit=function(copyElement,property='all'){let elementStyles
if(typeof copyElement=='object'){elementStyles=window.getComputedStyle(document.querySelector(copyElement.name))}else if(typeof copyElement=='string'){elementStyles=window.getComputedStyle(document.querySelector(copyElement))}
if(property=='all'){for(var i=0;i<elementStyles.length;i++){var prop=elementStyles[i]
this.element.css(prop,elementStyles[prop])}}else{this.element.css(property,elementStyles[property])}}